body {
  width: 100vw;
  height: 100vh;
  margin: 0;
  padding: 0;
  font: 0.8em ArialMT, Arial, Helvetica, sans-serif;
  font-size: 16px;
  color: #333;
  overflow-x: hidden;
}
.app {
  min-height: 100vh;
  display: grid;
  display: -ms-grid;
  grid-row-gap: calc(1.125rem + ((1vw - 3.75px) * 1.6145));
  grid-template-columns: 1fr;
  grid-template-rows: 92px auto 64px;
  grid-template-areas: "header" "content" "footer";
}
.header {
  grid-area: header;
  position: fixed;
}
.content {
  grid-area: content;
  display: flex;
  justify-content: center;
}
.footer {
  grid-area: footer;
}
.title {
  font-size: calc(1.5rem + ((1vw - 3.75px) * 1.2109));
  font-weight: bold;
  text-align: left;
}
.contain-img {
  -webkit-background-size: contain;
  background-size: contain;
}
.transition {
  transition: opacity 0.15s ease-in-out;
  -moz-transition: opacity 0.15s ease-in-out;
  -webkit-transition: opacity 0.15s ease-in-out;
}
.scrolling {
  overflow-x: auto;
  -webkit-overflow-scrolling: touch;
}
.scrolling::-webkit-scrollbar {
  display: none;
}
.dropdown {
  display: flex;
  flex-direction: column;
  position: absolute;
  padding: 15px;
  background: #FFF;
  border-radius: 5px;
  box-shadow: 0 2px 6px 0 rgba(197, 186, 186, 0.5);
}
.length-range-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 12px;
  height: 20px;
  border-radius: 5px;
  background: #E5E5E5;
  cursor: pointer;
}
.volume-range-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 16px;
  height: 16px;
  border-radius: 50%;
  background: #858585;
  cursor: pointer;
}
.videos {
  width: 100%;
  max-width: 1024px;
  margin: 0 40px;
}
.videos__title {
  margin-bottom: 32px;
}
.videos .all-cameras__title {
  cursor: pointer;
}
.videos__container {
  display: grid;
  grid-template-columns: repeat(auto-fill, 360px);
  grid-template-rows: repeat(auto-fill, 360px);
  grid-gap: 10px;
  justify-content: space-between;
  align-content: center;
  position: relative;
}
.videos .video {
  width: 100%;
  height: auto;
  display: flex;
  transition: all 2s ease;
}
.videos .video__container {
  transition: all 2s ease;
}
.videos .video__controls {
  display: none;
}
.videos .video_hidden {
  display: none;
}
.videos .video_selected {
  width: 100%;
  height: 100%;
  position: absolute;
  display: block;
  z-index: 1;
}
.videos .video_selected .video__controls {
  display: block;
}
.header {
  width: 100%;
  height: 92px;
  display: flex;
  align-items: center;
  position: fixed;
  z-index: 1;
  background: #FFF;
  box-shadow: 0 1px rgba(0, 0, 0, 0.1);
}
.header__logo {
  width: 108px;
  height: 2em;
  margin: 0 7px 0 40px;
  background: url("a6346f046c5aee7c1ab79b7a62329743.svg") no-repeat;
  -webkit-background-size: contain;
  background-size: contain;
}
.header__menu {
  display: none;
  width: 16px;
  height: 14px;
  background: url("90c232cc5a5a37ce042db07b87c917d8.svg") no-repeat;
  -webkit-background-size: contain;
  background-size: contain;
  cursor: pointer;
}
.header__nav {
  display: flex;
}
.header__nav-item {
  margin: 14px;
  font-weight: bold;
  line-height: 20px;
  color: #858585;
  text-decoration: none;
  transition: opacity 0.15s ease-in-out;
  -moz-transition: opacity 0.15s ease-in-out;
  -webkit-transition: opacity 0.15s ease-in-out;
}
.header__nav-item:hover {
  color: #333;
}
.header__nav-item_selected {
  color: #333;
}
.footer {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 40px;
  box-shadow: 0 -1px rgba(0, 0, 0, 0.1);
}
.footer__nav {
  display: flex;
}
.footer__nav-item {
  margin-right: 15px;
  font-size: 12px;
  letter-spacing: 0.01px;
  color: #858585;
  text-align: left;
  text-decoration: none;
  transition: opacity 0.15s ease-in-out;
  -moz-transition: opacity 0.15s ease-in-out;
  -webkit-transition: opacity 0.15s ease-in-out;
}
.footer__nav-item:hover {
  color: #333;
}
.footer__logo {
  font-size: 12px;
  letter-spacing: 0.01px;
  color: #858585;
}
.cards {
  max-width: 1024px;
  margin: 0 20px;
}
.cards__title {
  margin-bottom: 32px;
}
.cards__container {
  display: -ms-grid;
  display: grid;
  grid-template-columns: repeat(6, 1fr);
  grid-gap: 20px;
}
.card {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  flex-shrink: 0;
  padding: 20px;
  position: relative;
  border-radius: 20px;
  background: #FAFAFA;
  cursor: pointer;
}
.card:hover {
  background: #F3F3F3;
  box-shadow: 0 2px 6px 0 rgba(197, 186, 186, 0.5);
}
.card:hover .button__close,
.card:hover .button__next {
  display: block;
}
.card:hover .card__additional-content {
  background: #F3F3F3;
}
.card .button__close,
.card .button__next {
  display: none;
  width: 17px;
  height: 17px;
  position: absolute;
  right: 20px;
}
.card .button__close {
  top: 20px;
  background: url("27821d6f7649d60b6b59e202f991c3cc.svg") no-repeat;
}
.card .button__next {
  bottom: 20px;
  background: url("a8289b13e619519a1e7bb0c9fd8abe60.svg") no-repeat;
}
.card__additional-content {
  padding: 20px;
  margin: -20px;
  border-radius: 20px;
  background: #FAFAFA;
}
.card__title-group {
  display: flex;
  align-items: center;
  margin-bottom: 25px;
}
.card__title-group .card__title {
  font-size: calc(0.875rem + ((1vw - 3.75px) * 1.0091));
