  background: url("5a0223d29980e1282e17ba99da42c453.svg") no-repeat;
}
.card__title-group .card__icon-container .card__icon-router {
  height: 46px;
  background: url("3cdfa2336678508c4cf43c21cc468096.svg") no-repeat;
}
.card__title-group .card__icon-container .card__icon-thermal {
  height: 48px;
  background: url("3cdfa2336678508c4cf43c21cc468096.svg") no-repeat;
}
.card__title-group .card__icon-container .card__icon-ac {
  height: 41px;
  background: url("cec5d8940ec58db203c6274f453cec44.svg") no-repeat;
}
.card__title-group .card__icon-container .card__icon-music {
  height: 48px;
  background: url("a4ab0c46875639d970e117d50fef84c9.svg") no-repeat;
}
.card__title-group .card__icon-container .card__icon-fridge {
  height: 48px;
  background: url("72d52942e076545c0afb87f0d99dc47d.svg") no-repeat;
}
.card__title-group .card__icon-container .card__icon-battery {
  height: 25px;
  background: url("fb9fa58485a84f3f43360d14a4072c7d.svg") no-repeat;
}
.card__title-group .card__icon-container .card__icon-cam {
  height: 36px;
  background: url("26d07571f2e66962fb3d14987bfab520.svg") no-repeat;
}
.card__title-group .card__icon-container .card__icon-kettle {
  height: 48px;
  background: url("69337d5004905095add35d8ec4664cb4.svg") no-repeat;
}
.card__info {
  display: flex;
  justify-content: space-between;
}
.card__info__source,
.card__info__time {
  font-size: calc(0.75rem + ((1vw - 3.75px) * 0.6054));
}
.card__description {
  font-size: calc(1.125rem + ((1vw - 3.75px) * 0.2018));
}
.card_type_critical {
  color: #FFF;
  background: #DB5341;
}
.card_type_critical:hover {
  background: #DB5341;
}
.card_type_critical .card__additional-content {
  margin-top: 20px;
  color: #333;
}
.card__data .image {
  width: 100%;
  border-radius: 20px;
}
.card__data .graph__image {
  margin-top: 40px;
}
.card__data .camera__container {
  margin-top: 30px;
  overflow: hidden;
  border-radius: 20px;
  background-color: #b6a399;
}
.card__data .camera__container .camera__image {
  transform: scale(1) translate(0);
  filter: brightness(100%);
}
.card__data .camera__controls {
  display: none;
  justify-content: space-between;
  margin-top: 15px;
  font-size: 12px;
}
.card__data .thermal__container {
  margin-top: 22px;
  display: flex;
  font-size: calc(0.875rem + ((1vw - 3.75px) * 0.4036));
}
.card__data .thermal__container .thermal__temperature {
  margin-right: 52px;
}
.card__data .thermal__container .thermal__value {
  font-weight: bold;
}
.card__data .track__container {
  margin-top: 25px;
}
.card__data .track__container .track__info {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
}
.card__data .track__container .track__image {
  width: 52px;
  height: 52px;
}
.card__data .track__container .track__panel {
  flex-grow: 1;
  margin-left: 15px;
}
.card__data .track__container .track {
  margin-bottom: 10px;
  font-size: calc(1.125rem + ((1vw - 3.75px) * 0.2018));
}
.card__data .track__container .track__controls {
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.card__data .track__container .track__controls-text {
  font-size: 14px;
  color: #858585;
}
.card__data .track__container .track__length-control {
  flex-grow: 1;
  height: 0;
  margin-right: 16px;
  border: 1px solid #858585;
  -webkit-appearance: none;
  outline: none;
}
.card__data .track__container .track__length-control::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 12px;
  height: 20px;
  border-radius: 5px;
  background: #E5E5E5;
  cursor: pointer;
}
.card__data .track__container .track__length-control::-moz-range-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 12px;
  height: 20px;
  border-radius: 5px;
  background: #E5E5E5;
  cursor: pointer;
}
.card__data .track__container .track__length-control::-ms-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 12px;
  height: 20px;
  border-radius: 5px;
  background: #E5E5E5;
  cursor: pointer;
}
.card__data .track__container .track__previous,
.card__data .track__container .track__next {
  width: 52px;
  height: 52px;
  background: url("2e60d886a995d56eba69c90e14da4882.svg") no-repeat;
}
.card__data .track__container .track__next {
  transform: rotate(180deg);
  margin-left: 27px;
}
.card__data .track__container .track__volume-control {
  height: 4px;
  flex-grow: 1;
  margin: 0 20px;
  background: rgba(216, 216, 216, 0.3);
  border-radius: 2px;
  -webkit-appearance: none;
  outline: none;
}
.card__data .track__container .track__volume-control::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 16px;
  height: 16px;
  border-radius: 50%;
  background: #858585;
  cursor: pointer;
}
.card__data .track__container .track__volume-control::-moz-range-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 16px;
  height: 16px;
  border-radius: 50%;
  background: #858585;
  cursor: pointer;
}
.card__data .track__container .track__volume-control::-ms-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 16px;
  height: 16px;
  border-radius: 50%;
  background: #858585;
  cursor: pointer;
}
.card__data .buttons__container {
  display: flex;
  margin-top: 50px;
}
.card__data .buttons__container .button {
  width: 132px;
  height: 54px;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 8px;
  background: #E5E5E5;
  font-size: 20px;
  font-weight: bold;
}
.card__data .buttons__container .button__0 {
  margin-right: 18px;
  background: #FFD93E;
}
.card_size_l {
  grid-column: span 4;
  grid-row: span 3;
}
.card_size_l .card__description {
  font-size: calc(1.125rem + ((1vw - 3.75px) * 0.6054));
}
.card_size_m {
  grid-column: span 3;
}
.card_size_s {
  grid-column: span 2;
}
.card_size_s .card__info {
  flex-direction: column;
}
.card_size_s .card__info .card__source {
  margin-bottom: 20px;
}
@media only screen and (max-width: 1024px) {
  @supports (display: grid) {
    .app {
      grid-template-columns: 100%;
      grid-template-rows: 64px auto 64px;
      grid-template-areas: "header" "content" "footer";
    }
  }
  .header {
    height: 64px;
    justify-content: space-between;
    box-shadow: none;
  }
  .header__logo {
    margin: 0 20px;
  }
  .header__menu {
    display: inline-block;
    margin: 0 20px;
  }
  .header__nav {
    display: none;
  }
  .header__nav_visible {
    display: flex;
    flex-direction: column;
    position: absolute;
    padding: 15px;
    background: #FFF;
    border-radius: 5px;
    box-shadow: 0 2px 6px 0 rgba(197, 186, 186, 0.5);
    top: 40px;
    right: 10px;
  }
  .header .content .videos {
    margin: 0 20px;
  }
  .header .header__nav-item {
    margin: 5px;
    line-height: 15px;
  }
  .footer {
    padding: 0 20px;
    justify-content: space-around;
    flex-direction: column;
    align-items: unset;
  }
  .footer__nav {
    margin-top: 10px;
    flex-wrap: wrap;
  }
}
@media only screen and (max-width: 960px) {
  .cards__container {
    grid-gap: 16px;
  }
  .card_size_l,
  .card_size_m,
  .card_size_s {
    grid-column: span 6;
  }
}
